mapper.select:
select studnet_course.*,stduent.name as studentName from stduent_course left join student on stduent_course.student_id=studnet.id
and student_course.name like concat('%',#{name},'%') nd student_course.no like concat('%',#{no},'%') and student_course.stduent_id =#{stduentId}
order by id desc
